function save_options(){var pattern=document.getElementById("pattern").value,concurrent_downloads=document.getElementById("concurrent_downloads").value;chrome.storage.sync.set({pattern:pattern,concurrent_downloads:concurrent_downloads},function(){var status=document.getElementById("status");status.textContent="Options saved.",setTimeout(function(){status.textContent=""},750)})}function restore_options(){chrome.storage.sync.get({pattern:"*.jp*g",concurrent_downloads:5},function(items){document.getElementById("pattern").value=items.pattern,document.getElementById("concurrent_downloads").value=items.concurrent_downloads})}document.addEventListener("DOMContentLoaded",restore_options),document.getElementById("save").addEventListener("click",save_options);